frappe.ui.form.on('Purchase Invoice', {
    before_save: function(frm){
        var total_qty = frm.doc.number_of_coconant;
        var total_qty1 = frm.doc.total_qty_loaded;
        if(total_qty != total_qty1){
            frappe.throw("Total Qty is not matched to Loaded Qty")
        }
    },
    validate: function(frm){
        var total_dis = frm.doc.ending_km - frm.doc.starting_km;
        frm.set_value('total_distance',total_dis);
    },
//     tare_weight_per_bag: function(frm){
