//         frm.set_value('tare_weight',tot_tare);
//     },
    gross_weight: function(frm){
        var tare_tot = (frm.doc.gross_weight / 100) * 3;
        frm.set_value('tare_weight',tare_tot);
        var net_tot = frm.doc.gross_weight - tare_tot;
        frm.set_value('net_weight',net_tot)
    },
