frappe.ui.form.on("Loading Details", {
	qty: function(frm, cdt, cdn) {
		var d = locals[cdt][cdn];
		var total = 0;
        frappe.model.set_value(d.doctype, d.name, "qty", d.qty);        
        frm.doc.loading_.forEach(function(d){ total += d.qty; });
        frm.set_value('total_qty_loaded', total);
	}
});
frappe.ui.form.on("Purchase Invoice Item",{
    form_render: function(frm,cdt,cdn){
        let items = locals[cdt][cdn];
        var net_tot = frm.doc.net_weight;
        items.qty = net_tot;
    }
});
